class Animate{constructor({from:t=0,to:i=1,duration:n=300,ease:s=null,onUpdate:a,onEnd:e}={}){this.delta=i-t,this.startTime=performance.now(),this.from=t,this.to=i,this.duration=n,this.ease=s||this.easeOut,this.onEnd=e,this.onUpdate=a,this.init(performance.now())}init(t){this.animation=requestAnimationFrame(t=>this.init(t)),this.update(t)}update(t){const i=t-this.startTime,n=Math.min(i/this.duration,1);if(n>=1)return cancelAnimationFrame(this.animation),this.onEnd&&this.onEnd(),Math.floor(900*Math.random());const s=this.from+this.ease(n)*this.delta;this.onUpdate&&this.onUpdate(s,n)}easeOut(t,i){return 1-(1-t)**(i||2)}}
